int main(int argc, char **argv) {
    if (argc != 5) exit(1);
    registerGen(argc, argv, 1);

    int id = std::stoi(std::string(argv[4]));

    int N, X, Y;
    vector<int> A;

    if (id != 0) {
        A = special(id);
        N = (int)A.size();
        X = 1;
        Y = 2;
        for (int i = 0; i < N; i++) 
            A[i]++;
    }
    else {
        N = std::stoi(std::string(argv[1]));
        int cX = std::stoi(std::string(argv[2]));
        int cY = std::stoi(std::string(argv[3]));
        int cZ = N - cX - cY;
        
        for (int i = 1; i < MAX_A; i++) 
            mapping[i] = i;

        shuffle(mapping + 1, mapping + MAX_A);

        X = mapping[1];
        Y = mapping[2];

        for (int i = 0; i < cX; i++) 
            A.push_back(1);
        for (int i = 0; i < cY; i++)
            A.push_back(2);
        for (int i = 0; i < cZ; i++)
            A.push_back(Rnd(3, MAX_A - 1));
        for (int i = 0; i < N; i++) 
            A[i] = mapping[A[i]];
    }

    println(N, X, Y);
    int *begin = A.data();
    println(begin, begin + N);

    return 0;
}
